            key={filter}
            active={activeFilters.includes(filter)}
            onClick={() => toggleFilter(filter)}
          >
            {filter}
          </FilterChip>
        ))}
        {(activeFilters.length > 0 || searchQuery || selectedCategory || selectedLocation) && (
          <ClearFiltersButton onClick={clearFilters}>
            Clear All
          </ClearFiltersButton>
        )}
      </FiltersSection>

      <ResultsHeader>
        <ResultsCount>
          {filteredJobs.length} job{filteredJobs.length !== 1 ? 's' : ''} found
        </ResultsCount>
        <SortSelect value={sortBy} onChange={(e) => setSortBy(e.target.value)}>
          <option value="newest">Newest First</option>
          <option value="oldest">Oldest First</option>
          <option value="budget-high">Highest Budget</option>
          <option value="budget-low">Lowest Budget</option>
        </SortSelect>
      </ResultsHeader>

      {isLoading ? (
        <LoadingSpinner />
      ) : filteredJobs.length > 0 ? (
        <JobsGrid>
          {filteredJobs.map(job => (
            <JobCard key={job.id} to={`/jobs/${job.id}`}>
              <JobCategory>{job.category}</JobCategory>
              <JobHeader>
                <JobTitle>{job.title}</JobTitle>
                <JobBudget>{job.budget}</JobBudget>
              </JobHeader>
              <JobDescription>{job.description}</JobDescription>
              <JobMeta>
                <JobLocation>üìç {job.location}</JobLocation>
                <JobTime>{job.postedAt}</JobTime>
              </JobMeta>
            </JobCard>
          ))}
        </JobsGrid>
      ) : (
        <EmptyState>
          <EmptyIcon>üîç</EmptyIcon>
          <EmptyTitle>No jobs found</EmptyTitle>
          <EmptyText>
            Try adjusting your search criteria or filters to find more opportunities.
          </EmptyText>
          <ClearFiltersButton onClick={clearFilters}>
            Clear Filters
          </ClearFiltersButton>
        </EmptyState>
      )}
    </JobsContainer>
  );
};

