  border-color: ${({ theme }) => theme.colors.error}20;
`;

const DangerButton = styled.button`
  padding: ${({ theme }) => theme.spacing[3]} ${({ theme }) => theme.spacing[6]};
  background: ${({ theme }) => theme.colors.error};
  color: ${({ theme }) => theme.colors.white};
